        <span class="fc-red fs20">
          <b id="marketPrice">
            {{if stockVo.vipDiscount != ""}}
              {{if stockVo.minSkuPriceVip == stockVo.maxSkuPriceVip}}
              ¥{{stockVo.minSkuPriceVip | toFixed2}}
              {{else}}
              ¥{{stockVo.minSkuPriceVip | toFixed2}} ~ {{stockVo.maxSkuPriceVip | toFixed2}}
              {{/if}}
            {{else}}
              {{if stockVo.maxSkuPrice == stockVo.minSkuPrice}}
              ¥{{stockVo.minSkuPrice | toFixed2}}
              {{else}}
              ¥{{stockVo.minSkuPrice | toFixed2}} ~ {{stockVo.maxSkuPrice | toFixed2}}
              {{/if}}
            {{/if}}
          </b>
        </span>
        {{if stockVo.vipDiscount != ""}}
          <del class="fs16 ml5 mr5">
          {{if stockVo.maxSkuPrice == stockVo.minSkuPrice}}
          ¥{{stockVo.minSkuPrice | toFixed2}}
          {{else}}
          ¥{{stockVo.minSkuPrice | toFixed2}} ~ {{stockVo.maxSkuPrice | toFixed2}}
          {{/if}}
          </del>
        {{/if}}
        <del class="fs16">¥{{stockVo.stock.tagPrice | toFixed2}}</del>
      </div>
      {{if stockVo.vipDiscount != ""}}
      <div class="vipSkuTag">
          <div><i class="iconfont">&#xe61a;</i><span>{{stockVo.vipDiscount}}</span></div>
      </div>
      {{/if}}
      <div class="limitItems pl15 pr15">
        {{if stockVo.productSupplier.limitDelivery == 1 && stockVo.stock.sourceType != 1}}
          <span class="item">仅限到店自提</span>
        {{/if}}
        {{if stockVo.productSupplier.limitDelivery == 2 || stockVo.stock.sourceType == 1}}
        <span class="item">仅限快递发货</span>
        {{/if}}
        {{if stockVo.productSupplier.couponDisable == 1}}
        <span class="item">不支持优惠券</span>
        {{/if}}
        {{if stockVo.productSupplier.limitCount > 0 }}
        <span class="item">限购{{stockVo.productSupplier.limitCount}}件</span>
        {{/if}}
      </div>
      {{if promotion}}
      <a class="discountPromotion ub ub-ac" href="discountPromotion.htm?stockId={{stockVo.stock.id}}">
        <div class="promotionList ub-f1">
          <i class="iconfont fc-red fs20 mr5">&#xe651;</i>{{promotion.promotionName}}
        </div>
        <div class="block fc-red">进入活动专区</div>
      </a>
      {{/if}}
    </div>
  </section>
  {{if stockVo.flashsaleStock}}
  <section class="stockInfoBlock seckillGuide">
      <a href="getStockInfoForSeckill.htm?flashsaleStockId={{stockVo.flashsaleStock.id}}&stockId={{stockVo.flashsaleStock.id}}&supplierId={{stockVo.flashsaleStock.supplierId}}&suid={{stockVo.flashsaleStock.supplierId}}" class="fc-red fs16"></a>
  </section>
  {{/if}}
  <section class="stockInfoBlock" style="margin:0;padding:.1rem 0;">
    <input id="productPicUrl" type="hidden" value="{{stockVo.productSupplier.picUrl}}">
    <div class="salesInfo pl15 pr15">
      <div class="fs16 tl" style="padding-bottom:.1rem;">店铺信息</div>
      <div class="ub ub-ac tl">
        <div class="img_bor ub ub-ac ub-pc"><img src="http://static.qiakr.com/FtR326cWZShNZ5LeXMg0b7WDcCZD" class="size43"></div>
        <div class="ub-f1 pl5">
          <div class="fs16 mb5">施舜天河店</div>
          <div class="address p0 fs14">广东省 广州市 天河区 广州天河南一路42号之101房  联系电话：87516976</div>
        </div>
        <a class="btn btn-red" style="padding:5px;" href="getStoreHomePage.htm?storeId=191"><i class="iconfont mr5 fs16">&#xe62c;</i>进入店铺</a>
      </div>
    </div>
        </div>

    <a class="hide" id="pageChat" href="contactSales.htm?brandId={{stockVo.product.brandId}}&supplierId={{stockVo.supplier.id}}&storeId=191"></a>
      </section>
  <div class="tl">
    <div class="detailTitle">图文详情</div>
    <div class="descriptionContainer tl pl15 pr15">
      {{if stockVo.productSupplier.description}}
      {{stockVo.productSupplier.description}}
      {{else}}
      <p class="c-9 pt20 pb20 tc">主人很懒，什么都没有编写</p>
      {{/if}}
    </div>
    <div class="detailTitle">商品评价</div>
    <div id="appriseContainer"><p class="c-9 pt20 pb20">暂无评价记录~</p></div>
    <div class="detailTitle">相关推荐</div>
    <div id="recommendContainer"></div>
  </div>
</script>

<!-- 评价模板 -->
<script id="tpl_appraise" type="text/html">
{{each list as e i}}
<div class="item tl">
  <div class="wbox">
    <div class="wbox-1">
      <div class="fs16">{{e.customer.name}}</div>
      <div class="c-9">{{e.appraiseStock.gmtCreate | dayFormat}}</div>
    </div>
    <div class="starContainer">
      <div class="stars" style="width:{{e.appraiseStock.stars | starsFormat}}%;"></div>
    </div>
  </div>
  <div class="pt10 c-8 lh24">{{e.appraiseStock.comment}}</div>
</div>
{{/each}}
<div class="tc pt10"><a href="getCommentOfProduct.htm?stockId={{stockId}}&from={{from}}" class="btn appriseBtn">查看全部评价({{count}})</a></div>
</script> 
<!-- 商品推荐模板 -->
<script id="tpl_recommend" type="text/html">
{{each list as e i}}
<div class="item">
  <a href="getStockInfoForCustomer.htm?stockId={{e.stock.id}}&from={{from}}" class="img" style="background-image:url({{if e.stock.productPicUrl}}{{e.stock.productPicUrl}}?imageView2/1/h/400{{/if}});"></a>
  <div class="name">{{e.stock.productName}}</div>
  <div class="pt5 pb5">
    <span class=" fc-red fs16">¥{{e.minSkuPrice | toFixed2}}</span>
    <del class="fs16 c-9">¥{{e.stock.tagPrice | toFixed2}}</del>
  </div>
</div>
{{/each}}
</script>

<!-- 规格选择模板 -->
<script id="tpl_skuSelect" type="text/html">
<a href="javascript:;" class="closeMask"><i class="iconfont">&#xe644;</i></a>
<div id="skuBoxWrap">  
  <div class="p20">
    <div class="stockMsg wbox pb10">
      <div class="img" style="background-image:url({{stockVo.productSupplier.picUrl}})"></div>
      <div class="wbox-1">
        <p class="c-0">{{stockVo.productSupplier.name}}</p>
        <p class="fc-red fs16 pt5" data-oldprice="{{stockVo.minSkuPrice}}" id="skuPrice">
        {{if stockVo.vipDiscount != ""}}
          {{if stockVo.minSkuPriceVip == stockVo.maxSkuPriceVip}}
          ¥{{stockVo.minSkuPriceVip | toFixed2}}
          {{else}}
          ¥{{stockVo.minSkuPriceVip | toFixed2}} ~ {{stockVo.maxSkuPriceVip | toFixed2}}
          {{/if}}
          <span class="sku-tag"></span>
        {{else}}
          {{if stockVo.maxSkuPrice == stockVo.minSkuPrice}}
          ¥{{stockVo.minSkuPrice | toFixed2}}
          {{else}}
          ¥{{stockVo.minSkuPrice | toFixed2}} ~ {{stockVo.maxSkuPrice | toFixed2}}
          {{/if}}
        {{/if}}
        </p>
        <p class="pt5">剩余库存<span id="skuCountLast"></span>件</p>
      </div>
    </div>
    <p class="skuTitle">收货方式</p>
    <div class="skuContainer" id="getStockType">
      {{if stockVo.stock.sourceType == 1}}
        <i class="skuBox" data-type="1">快递发货</i>
      {{else}}
        {{if stockVo.productSupplier.limitDelivery != 1}}
          <i class="skuBox" data-type="1">快递发货</i>
        {{/if}}
        {{if stockVo.productSupplier.limitDelivery != 2}}
          <i class="skuBox" data-type="2">到店自提</i>
        {{/if}}
      {{/if}}
    </div>
    {{if stockVo.productSupplier.norms1Id}}
      <p class="skuTitle">{{stockVo.productSupplier.norms1Id | normsFormat}}</p>
      <div class="skuContainer" id="colorSkus">
        {{each norms1List as e i}}
          <i class="skuBox" title="{{e}}">{{e}}</i>
        {{/each}}
      </div>
    {{/if}}
    {{if stockVo.productSupplier.norms2Id}}
      <p class="skuTitle">{{stockVo.productSupplier.norms2Id | normsFormat}}</p>
      <div class="skuContainer" id="sizeSkus">
      {{each norms2List as e i}}
        <i class="skuBox" title="{{e}}">{{e}}</i>
      {{/each}}
      </div>
    {{/if}}
    <p class="skuTitle">数量 {{if stockVo.productSupplier.limitCount}}<span class="fc-red">每人限购{{stockVo.productSupplier.limitCount}}件</span>{{/if}}</p>
    <div class="countController wbox">
      <a href="javascript:;" class="wbox-1 sub"><i class="iconfont">&#xe654;</i></a>
      <input type="number" class="wbox-1 tc" id="countInput" value="1">
      <a href="javascript:;" class="wbox-1 add"><i class="iconfont">&#xe655;</i></a>
    </div>
  </div>
  <a href="javascript:;" class="btn btn-red full disabled" id="skuConfirmBtn">确定</a>
</div>
</script>


</body></html><?php }
}
